mapper.select:
select g.group_id, g.group_name, g.comment, g.content , g.edit_date,u.name as creater_name from groups g inner join users u on g.user_id=u.id
g.user_id=#{userId}
order by g.group_id

select g.group_id, g.group_name, g.comment, g.content , g.edit_date from groups g
 inner join group_users gu on gu.group_id=g.group_id

    gu.user_id=#{userId}
